CREATE TABLE usuarios(id int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,apodo VARCHAR(100) NOT NULL,nombre VARCHAR(100) NOT NULL,correo VARCHAR(100) NOT NULL,contrasena VARCHAR(50) NOT NULL);

CREATE TABLE punto(id INT(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,nombre VARCHAR(100) NOT NULL,liga VARCHAR(200) NOT NULL,fecha DATE NOT NULL,hora TIME NOT NULL, id_usuario INT (11) NOT NULL, FOREIGN KEY (id_usuario) REFERENCES usuarios(id));